import React, { useEffect, useState } from 'react';
import axios from 'axios';
import './MatchList.css'; // Add this CSS file too

export default function MatchList({ username }) {
  const [matches, setMatches] = useState([]);

  // useEffect(() => {
  //   if (username) {
  //     fetchMatches();
  //   }
  // }, [username]);

  const fetchMatches = async () => {
    const res = await axios.get(`http://localhost:3001/matches/${username}`);
    setMatches(res.data);
  };

  const handleDelete = async (nameToDelete) => {
    await axios.delete(`http://localhost:3001/users/${nameToDelete}`);
    fetchMatches();
  };

  return (
    <div className="match-container">
      <h2>Matches for {username}</h2>
      {matches.length === 0 ? (
        <p className="empty">No matches found yet.</p>
      ) : (
        matches.map((match, i) => (
          <div key={i} className="match-card">
            <h3>{match.name}</h3>
            <p>{match.interests.join(', ')}</p>
            <button onClick={() => handleDelete(match.name)}>‚ùå Delete</button>
          </div>
        ))
      )}
    </div>
  );
}
