import React, { useEffect, useState } from 'react';
import axios from 'axios';
import './MatchList.css';

export default function MatchList({ username }) {
  const [matches, setMatches] = useState([]);

  useEffect(() => {
    fetchMatches();
  }, [username]);

  const fetchMatches = async () => {
    try {
      const res = await axios.get(`http://localhost:3001/matches/${username}`);
      setMatches(res.data);
    } catch (err) {
      console.error('Error fetching matches:', err);
    }
  };

  const handleConnect = async (matchName) => {
    try {
      await axios.delete(`http://localhost:3001/users/${matchName}`);
      fetchMatches(); // refresh match list
    } catch (err) {
      console.error('Error connecting with user:', err);
    }
  };

  return (
    <div className="match-list">
      <h2>ðŸ’– Matches for {username}</h2>
      <div className="card-wrapper">
        {matches.length === 0 ? (
          <p>No matches yet.</p>
        ) : (
          matches.map((match, index) => (
            <div className="match-card" key={index}>
              <h3>{match.name}</h3>
              <p>Interests: {match.interests.join(', ')}</p>
              <button className="connect-btn" onClick={() => handleConnect(match.name)}>
                ðŸ’˜ Connect
              </button>
            </div>
          ))
        )}
      </div>
    </div>
  );
}
