import React, { useState } from 'react';
import UserForm from './components/UserForm';
import MatchList from './components/MatchList';

function App() {
  const [username, setUsername] = useState('');
  const [showMatches, setShowMatches] = useState(false);

  return (
    <div>
      <UserForm onCreated={(name) => {
        setUsername(name);
        setShowMatches(false); // hide matches initially
      }} />

      {username && !showMatches && (
        <div style={{ textAlign: 'center', marginTop: '20px' }}>
          <button
            onClick={() => setShowMatches(true)}
            style={{
              padding: '12px 24px',
              background: '#e91e63',
              color: '#fff',
              border: 'none',
              borderRadius: '10px',
              fontSize: '16px',
              cursor: 'pointer'
            }}
          >
            ðŸ’˜ Connect
          </button>
        </div>
      )}

      {username && showMatches && <MatchList username={username} />}
    </div>
  );
}

export default App;
