import React, { useEffect, useState } from 'react';
import axios from 'axios';

export default function MatchList({ username }) {
  const [matches, setMatches] = useState([]);

  // Function to fetch matches for the current user
  const fetchMatches = async () => {
    try {
      const res = await axios.get(`http://localhost:3001/matches/${username}`);
      setMatches(res.data);
    } catch (err) {
      console.error("Failed to fetch matches", err);
    }
  };

  // Fetch matches whenever the username changes
  useEffect(() => {
    if (username) {
      fetchMatches();
    }
  }, [username]);

  // Delete a user and refresh the match list
  const handleDelete = async (nameToDelete) => {
    try {
      await axios.delete(`http://localhost:3001/users/${nameToDelete}`);
      alert(`Deleted ${nameToDelete}`);
      fetchMatches(); // refresh list after deletion
    } catch (err) {
      alert('Failed to delete user');
    }
  };

  return (
    <div>
      <h2>Matches for {username}</h2>
      {matches.length === 0 && <p>No matches found yet.</p>}
      {matches.map((m, index) => (
        <div key={index} className="match-card">
          <strong>{m.name}</strong>
          <p>{m.interests.join(', ')}</p>
          <div style={{ display: 'flex', gap: '10px', marginTop: '8px' }}>
            <button>Shortlist</button>
            <button
              style={{
                backgroundColor: '#e53935',
                color: 'white',
                border: 'none',
                padding: '6px 12px',
                borderRadius: '5px',
                cursor: 'pointer'
              }}
              onClick={() => handleDelete(m.name)}
            >
              Delete
            </button>
          </div>
        </div>
      ))}
    </div>
  );
}
