import React, { useState } from 'react';
import axios from 'axios';

export default function UserForm({ onCreated }) {
  const [form, setForm] = useState({ name: '', age: '', interests: '' });

  const handleChange = e => {
    setForm({ ...form, [e.target.name]: e.target.value });
  };

  const handleSubmit = async e => {
    e.preventDefault();
    const interestsArray = form.interests.split(',').map(i => i.trim());
    await axios.post('http://localhost:3001/users', {
      name: form.name,
      age: parseInt(form.age),
      interests: interestsArray
    });
    onCreated(form.name);
  };

  return (
    <form onSubmit={handleSubmit} style={{ marginBottom: '20px' }}>
      <h2>Create Your Profile</h2>
      <input name="name" placeholder="Name" onChange={handleChange} required />
      <br />
      <input name="age" type="number" placeholder="Age" onChange={handleChange} required />
      <br />
      <input name="interests" placeholder="Interests (comma-separated)" onChange={handleChange} required />
      <br />
      <button type="submit">Submit</button>
    </form>
  );
}
