import React, { useState } from 'react';
import axios from 'axios';
import './UserForm.css'; // Add styles here

export default function UserForm({ onCreated }) {
  const [form, setForm] = useState({ name: '', age: '', interests: '' });

  const handleChange = (e) => {
    setForm({ ...form, [e.target.name]: e.target.value });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    const interestsArray = form.interests.split(',').map((i) => i.trim());
    await axios.post('http://localhost:3001/users', {
      name: form.name,
      age: parseInt(form.age),
      interests: interestsArray,
    });
    onCreated(form.name);
  };

  return (
    <form onSubmit={handleSubmit} className="user-form">
      <h2>Create Your Profile</h2>
      <input name="name" onChange={handleChange} placeholder="Name" required />
      <input name="age" onChange={handleChange} type="number" placeholder="Age" required />
      <input
        name="interests"
        onChange={handleChange}
        placeholder="Interests (comma-separated)"
        required
      />
      <button type="submit">Submit</button>
    </form>
  );
}
